  const buffer = Buffer.from(originalFile.data, 'base64');

  return [{
    json: body,
    binary: {
      data: {
        data: buffer,  // Now it's a Buffer, not base64 string
        mimeType: originalFile.mimetype,
        fileName: originalFile.filename,
        fileExtension: originalFile.filename.split('.').pop()
