      <h2>Collection Method</h2>
      <div class="collection-badge">${collectionMethod}</div>
      ${isDelivery ? '<p style="margin-top: 10px; color: #856404;">‚ö†Ô∏è Arrange delivery with customer</p>' : '<p style="margin-top: 10px; color: #155724;">Customer will collect from store</p>'}
    </div>

    ${quote.Notes ? `
    <div class="section notes-section">
      <h2>üìù Notes</h2>
      <p style="font-size: 16px; white-space: pre-wrap;">${quote.Notes}</p>
    </div>
    ` : ''}

    <div class="section" style="padding: 0;">
